import { useEffect, useRef } from 'react';

interface ProgressBarProps {
  value: number;
  max: number;
  className?: string;
  showText?: boolean;
}

export default function ProgressBar({ value, max, className = '', showText = true }: ProgressBarProps) {
  const progressRef = useRef<HTMLDivElement>(null);
  const percentage = Math.min((value / max) * 100, 100);

  useEffect(() => {
    if (progressRef.current) {
      progressRef.current.style.setProperty('--progress-width', `${percentage}%`);
      // Trigger animation
      progressRef.current.classList.add('progress-weight');
    }
  }, [percentage]);

  return (
    <div className={`space-y-2 ${className}`}>
      {showText && (
        <div className="flex justify-between text-sm">
          <span className="text-muted-foreground">Progress</span>
          <span className="font-medium text-foreground">{Math.round(percentage)}%</span>
        </div>
      )}
      <div className="w-full bg-muted rounded-full h-3">
        <div 
          ref={progressRef}
          className="h-3 rounded-full transition-all duration-300"
          style={{ 
            background: 'linear-gradient(90deg, var(--primary) 0%, #f59e0b 100%)',
            width: `${percentage}%`
          }}
        />
      </div>
      {showText && (
        <div className="flex items-center space-x-2 mt-2">
          <div className="weight-plate" />
          <span className="text-xs text-muted-foreground">{value}/{max} completed</span>
        </div>
      )}
    </div>
  );
}

client/src/components/sql-editor.tsx.content:
import { useState } from 'react';
import { Play, Save, RotateCcw, Lightbulb } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';

interface SQLEditorProps {
  initialQuery?: string;
  onRunQuery: (query: string) => Promise<any>;
  hints?: string[];
  className?: string;
}

export default function SQLEditor({ 
  initialQuery = '', 
  onRunQuery, 
  hints = [],
  className = '' 
}: SQLEditorProps) {
  const [query, setQuery] = useState(initialQuery);
  const [result, setResult] = useState<any>(null);
  const [isRunning, setIsRunning] = useState(false);
  const [showHint, setShowHint] = useState(false);
  const [hintIndex, setHintIndex] = useState(0);

  const handleRunQuery = async () => {
    if (!query.trim()) return;

    setIsRunning(true);
    try {
      const result = await onRunQuery(query);
      setResult(result);
    } catch (error) {
      setResult({
        error: true,
        message: error instanceof Error ? error.message : 'Query execution failed',
      });
    } finally {
      setIsRunning(false);
    }
  };

  const handleSave = () => {
    localStorage.setItem('sqlgym_last_query', query);
    // Could also save to user's drafts via API
  };

  const handleReset = () => {
    setQuery(initialQuery);
    setResult(null);
  };

  const handleShowHint = () => {
    setShowHint(true);
  };

  const handleNextHint = () => {
    if (hintIndex < hints.length - 1) {
      setHintIndex(hintIndex + 1);
    }
  };

  return (
    <div className={`space-y-6 ${className}`}>
      {/* SQL Editor */}
      <div>
        <h3 className="text-xl font-semibold text-foreground mb-3">SQL Editor</h3>
        <Card className="overflow-hidden">
          <CardHeader className="bg-muted px-4 py-2 border-b border-border">
            <div className="flex items-center space-x-2">
              <div className="w-3 h-3 bg-red-400 rounded-full"></div>
              <div className="w-3 h-3 bg-yellow-400 rounded-full"></div>
              <div className="w-3 h-3 bg-green-400 rounded-full"></div>
              <span className="text-sm text-muted-foreground ml-4">query.sql</span>
            </div>
          </CardHeader>
          <CardContent className="p-0">
            <Textarea
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="w-full min-h-64 p-4 code-editor resize-none focus:outline-none border-0 rounded-none"
              placeholder="-- Write your SQL query here
SELECT 
    column1,
    column2
FROM table_name
WHERE condition;"
              data-testid="textarea-sql-query"
            />
          </CardContent>
        </Card>
      </div>

      {/* Action Buttons */}
      <div className="flex flex-wrap gap-3">
        <Button
          onClick={handleRunQuery}
          disabled={isRunning || !query.trim()}
          className="dumbbell-btn bg-primary text-primary-foreground hover:bg-primary/90"
          data-testid="button-run-query"
        >
          <Play className="mr-2 h-4 w-4" />
          {isRunning ? 'Running...' : 'Run Query'}
        </Button>

        <Button 
          onClick={handleSave} 
          variant="outline"
          data-testid="button-save"
        >
          <Save className="mr-2 h-4 w-4" />
          Save
        </Button>

        <Button 
          onClick={handleReset} 
          variant="outline"
          data-testid="button-reset"
        >
          <RotateCcw className="mr-2 h-4 w-4" />
          Reset
        </Button>

        {hints.length > 0 && (
          <Button 
            onClick={handleShowHint} 
            variant="outline"
            className="text-primary hover:bg-primary/10"
            data-testid="button-show-hint"
          >
            <Lightbulb className="mr-2 h-4 w-4" />
            Show Hint
          </Button>
        )}
      </div>

      {/* Hints */}
      {showHint && hints.length > 0 && (
        <Alert className="border-primary/20 bg-primary/10">
          <Lightbulb className="h-4 w-4 text-primary" />
          <AlertDescription className="text-foreground">
            <strong>Hint {hintIndex + 1}:</strong> {hints[hintIndex]}
            {hintIndex < hints.length - 1 && (
              <Button 
                onClick={handleNextHint}
                variant="link" 
                className="p-0 ml-2 text-primary"
                data-testid="button-next-hint"
              >
                Next hint →
              </Button>
            )}
          </AlertDescription>
        </Alert>
      )}

      {/* Results Panel */}
      {result && (
        <div>
          <h3 className="text-xl font-semibold text-foreground mb-3">Results</h3>
          <Card className="overflow-hidden">
            <CardHeader className={`px-4 py-2 border-b ${
              result.error ? 'bg-red-50 border-red-200' : 'bg-green-50 border-green-200'
            }`}>
              <div className="flex items-center space-x-2">
                {result.error ? (
                  <>
                    <div className="w-4 h-4 bg-red-500 rounded-full"></div>
                    <span className="text-red-800 font-medium">Query failed</span>
                  </>
                ) : (
                  <>
                    <div className="w-4 h-4 bg-green-500 rounded-full"></div>
                    <span className="text-green-800 font-medium">
                      {result.isCorrect ? 'Query executed successfully!' : 'Query executed, but result may be incorrect'}
                    </span>
                  </>
                )}
              </div>
            </CardHeader>
            <CardContent className="p-4">
              {result.error ? (
                <p className="text-red-600 text-sm">{result.message}</p>
              ) : (
                <div>
                  <p className="text-sm text-muted-foreground mb-2">
                    Execution time: {result.executionTime || 0}ms
                  </p>
                  {result.isCorrect && (
                    <Alert className="border-green-200 bg-green-50">
                      <AlertDescription className="text-green-800">
                        🎉 Congratulations! Your query is correct. You've earned XP points!
                      </AlertDescription>
                    </Alert>
                  )}
                  <div className="mt-4 text-sm text-muted-foreground">
                    <p>Query result would be displayed here in a real implementation.</p>
                    <p>Status: {result.isCorrect ? '✅ Correct' : '❌ Incorrect'}</p>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

